import { useAuth0 } from "@auth0/auth0-react";
import { LoginButton } from "./Login";
import { LogoutButton } from "./Logout";
import { Profile } from "./Profile";
import React from 'react';
import { Container, Typography, Button, AppBar, Toolbar, IconButton } from '@mui/material';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import MyIcon from './my-icon.png'; // Importa tu icono personalizado aquí

const Home = () => {
  const { isAuthenticated } = useAuth0();

  return (
    <div style={{ backgroundColor: '#FFD700' }}>
      <AppBar color="default" position="static">
        <Container>
          <Toolbar>
            <IconButton edge="start" color="inherit" aria-label="menu">
              <img src={MyIcon} alt="Mi Icono" style={{ width: '24px', height: '24px' }} />
            </IconButton>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              <span style={{ verticalAlign: 'middle' }}>Mi Página</span>
            </Typography>
            <Button color="inherit" style={{ color: '#FF4500' }}>
              Inicio
            </Button>  
            
            {/* Agrega aquí más elementos de menú según tus necesidades */}
          </Toolbar>
        </Container>
      </AppBar>

      <Carousel>
        <div>
          <img src="imagen1.jpg" alt="Imagen 1" />
          <p className="legend">Descripción de la imagen 1</p>
        </div>
        <div>
          <img src="imagen2.jpg" alt="Imagen 2" />
          <p className="legend">Descripción de la imagen 2</p>
        </div>
        {/* Agrega aquí más diapositivas según tus necesidades */}
      </Carousel>

      {/* Agrega aquí el contenido adicional que desees mostrar */}

      <div
        style={{
          backgroundColor: '#FFC0CB',
          padding: '20px',
          textAlign: 'center',
          fontSize: '14px',
          color: '#00BFFF',
        }}
      >
        <Typography variant="body2" color="text.secondary">
          Derechos de autor &copy; 2023. Todos los derechos reservados.
        </Typography>
      </div>
    </div>
  );
}

export default Home;
